  content: "";
  position: absolute;
  /* Making pseudoelement the same size as container */
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  /* Inheriting border properties */
  border-radius: inherit;
  border: inherit;
  /* Applying filter with shadow */
